import React from 'react';
import { postSale } from '../services/api';

const CheckoutPanel = ({ cart, setCart }) => {
  const total = cart.reduce((sum, item) => sum + item.qty * item.price, 0);

const handlePay = async () => {
  try {
    await postSale({
      total,
      items: cart.map(item => ({
        product_id: item.id,
        quantity: item.qty,
        price: item.price
      }))
    });
    alert(`✅ Paid ${total} MMK`);
  } catch (err) {
    console.error('❌ Failed to sync. Saving to local:', err);

    const existing = JSON.parse(localStorage.getItem('pending_sales') || '[]');
    existing.push({
      total,
      items: cart.map(item => ({
        product_id: item.id,
        quantity: item.qty,
        price: item.price
      }))
    });

    localStorage.setItem('pending_sales', JSON.stringify(existing));
    alert('❌ Failed to sync. Saved to local storage for later.');
  }
};


  return (
    <div className="checkout-panel">
      <h3>Total: {total} MMK</h3>
      <button onClick={handlePay}>Pay</button>
    </div>
  );
};

export default CheckoutPanel;
