import { postSale } from '../services/api';

const CheckoutPanel = ({ cart }) => {
  const handleCheckout = async () => {
    try {
      await postSale({ items: cart });
      alert("Sale saved!");
    } catch (err) {
      alert("Error saving sale!");
    }
  };

  return <button onClick={handleCheckout}>Checkout</button>;
};
